    /* update faceDetection Info */
    const int masterNodeIndex = OUTPUT_NODE_1, slaveNodeIndex = OUTPUT_NODE_2;
    const int masterCameraId = frame->getCameraId(masterNodeIndex), slaveCameraId = frame->getCameraId(slaveNodeIndex);
